using System;using System.Collections.Generic;using System.IO;using System.Text;using System.Linq;namespace Lab6_2{    class PlayerItem : IMedia    {        public List<string> items { get; private set; }             public void Search()        {            items = new List<string>();            foreach (var i in Directory.GetFiles(Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "media"), "*.mp3"))            {                items.Add(i);            }            foreach (var i in Directory.GetFiles(Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "media"), "*.wav"))            {                items.Add(i);            }            foreach (var i in Directory.GetFiles(Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "media"), "*.mkv"))            {                items.Add(i);            }            for(int i = 0; i < items.Count; i++)            {                Console.WriteLine(i + " " + Path.GetFileName(items[i])) ;            }        }                          }}